parts.text:
{
"word": "appear",
"priority": "★★★",
"meanings": [
{
"part_of_speech": "verb",
"transitivity": "vi",
"definition": "現れる、姿を現す、出現する",
"english_definition": "to become visible or noticeable, especially without apparent cause",
"examples": [
{
"sentence": "Suddenly, a cat appeared from behind the bush.",
"translation": "突然、猫が茂みの後ろから現れた。"
},
{
"sentence": "The sun appeared after the rain.",
"translation": "雨上がりに太陽が現れた。"
},
{
"sentence": "He appeared in the doorway.",
"translation": "彼は戸口に姿を現した。"
}
],
"collocations": [
"appear suddenly",
"appear unexpectedly",
"appear on the horizon",
"appear from nowhere"
],
"synonyms": [
"emerge",
"materialize",
"surface",
"show up"
],
"antonyms": [
"disappear",
"vanish"
]
},
{
"part_of_speech": "verb",
"transitivity": "vi",
"definition": "～のように見える、思われる、～らしい",
"english_definition": "to give the impression of being or doing something",
"examples": [
{
"sentence": "She appears to be tired.",
"translation": "彼女は疲れているように見える。"
},
{
"sentence": "It appears that he is telling the truth.",
"translation": "彼は真実を語っているように思われる。"
},
{
"sentence": "Things are not always as they appear.",
"translation": "物事は必ずしも見かけ通りではない。"
}
],
"collocations": [
"appear to be",
"appear that",
"appear as if",
"appear likely",
"appear certain"
],
"synonyms": [
"seem",
"look",
"sound",
"strike one as"
],
"antonyms": []
},
{
"part_of_speech": "verb",
"transitivity": "vi",
"definition": "（本・記事などが）掲載される、載る",
"english_definition": "to be published",
"examples": [
{
"sentence": "His article appeared in the newspaper.",
"translation": "彼の記事が新聞に掲載された。"
},
{
"sentence": "The advertisement will appear in next week's magazine.",
"translation": "その広告は来週の雑誌に掲載される予定だ。"
}
],
"collocations": [
"appear in a magazine",
"appear in a newspaper",
"appear online",
"appear on television"
],
"synonyms": [
"be published",
"be printed",
"be featured"
],
"antonyms": []
},
{
"part_of_speech": "verb",
"transitivity": "vi",
"definition": "（法廷などに）出廷する、出頭する",
"english_definition": "to attend a court of law or a formal meeting",
"examples": [
{
"sentence": "He appeared in court to face the charges.",
"translation": "彼は容疑に直面するため法廷に出廷した。"
},
{
"sentence": "The witness will appear before the committee tomorrow.",
"translation": "証人は明日委員会に出頭する予定だ。"
}
],
"collocations": [
"appear in court",
"appear before a judge",
"appear as a witness"
],
"synonyms": [
"attend",
"present oneself",
"show up"
],
"antonyms": []
},
{
"part_of_speech": "verb",
"transitivity": "vi",
"definition": "（テレビ・舞台などに）出演する",
"english_definition": "to perform or take part in a play, film, television program, etc.",
"examples": [
{
"sentence": "She appeared in several films.",
"translation": "彼女はいくつかの映画に出演した。"
},
{
"sentence": "He will appear on the talk show tonight.",
"translation": "彼は今夜、トーク番組に出演する予定だ。"
}
],
"collocations": [
"appear on television",
"appear in a film",
"appear on stage"
],
"synonyms": [
"perform",
"act",
"star"
],
"antonyms": []
}
],
"etymology": {
"value": "古フランス語の「apereir」（現れる、姿を現す）に由来し、さらにラテン語の「apparere」（現れる、見える）から派生。ラテン語の「ad-」（～へ）と「parere」（現れる、見える）が組み合わさったもの。",
"priority": "★★☆"
},
"pronunciation": {
"ipa": "/əˈpɪər/",
"syllables": "ap-pear"
},
"inflection": {
"verb_forms": {
"present_simple": [
"appear",
"appears"
],
"past_simple": "appeared",
"past_participle": "appeared",
"present_participle": "appearing"
}
},
"usage_notes": {
"explanation": "「**appear**」は、何かが視覚的に認識できるようになる、または存在し始めることを意味する基本的な動詞です。また、主観的な印象や推測を表す際にも使用されます（～のように見える）。さらに、記事や広告などが掲載される、法廷や会議に出席する、舞台やテレビに出演するなど、幅広い文脈で使用されます。自動詞としてのみ使用され、後に続く前置詞やto不定詞によって意味が変化することがあります。",
"priority": "★★★"
},
"common_mistakes": {
"examples": [
{
"incorrect": "✗ He appears that he is tired.",
"correct": "✓ He appears to be tired. / ✓ It appears that he is tired.",
"note": "「appear」の後に直接「that」節を続けることはできません。「appear to be」の形を使うか、「It appears that」の構文を使用します。"
},
{
"incorrect": "✗ She appeared to the court.",
"correct": "✓ She appeared in court.",
"note": "法廷に出廷する場合は、「appear in court」という表現を使います。"
},
{
"incorrect": "✗ The book appeared on the market.",
"correct": "✓ The book appeared on the shelves. / ✓ The book was released on the market.",
"note": "本が店頭に並ぶ場合は「appear on the shelves」を使い、市場に投入される場合は「be released on the market」を使うのがより適切です。"
}
],
"priority": "★★☆"
},
"related_words": {
"derivatives": [
"appearance (名詞: 外見、出現)",
"apparent (形容詞: 明らかな、明白な、外見上の)",
"apparently (副詞: 見たところ、どうやら)",
"reappear (動詞: 再び現れる)",
"disappear (動詞: 消える)"
],
"phrasal_verbs": [],
"priority": "★★★"
},
"level_frequency": {
"CEFR": "A2",
"frequency_google_ngram": "高頻度 (High frequency)",
"priority": "★☆☆"
},
"readability_explanation": {
"level": "A2",
"text": "「**appear**」は、何かが「見える」または「現れる」という意味の単語です。例えば、「The sun appeared.（太陽が現れた）」のように使います。また、「～のように見える」という意味でも使い、「She appears happy.（彼女は幸せそうに見える）」のように言えます。本や記事が「掲載される」という意味もあります。基本的な単語なので、意味と使い方を覚えましょう。"
},
"example_sentences": [
{
"sentence": "Stars **appear** in the sky at night.",
"translation": "夜になると星が空に現れる。",
"type": "verb",
"meaning_category": "現れる"
},
{
"sentence": "He **appears** calm, but he is actually very nervous.",
"translation": "彼は落ち着いているように見えるが、実際にはとても緊張している。",
"type": "verb",
"meaning_category": "～のように見える"
},
{
"sentence": "The new edition of the book will **appear** next month.",
"translation": "その本の新しい版は来月出版される予定だ。",
"type": "verb",
"meaning_category": "掲載される"
},
{
"sentence": "She had to **appear** in court as a witness.",
"translation": "彼女は証人として法廷に出廷しなければならなかった。",
"type": "verb",
"meaning_category": "出廷する"
},
{
"sentence": "The actor will **appear** on stage tonight.",
"translation": "その俳優は今夜舞台に出演する。",
"type": "verb",
"meaning_category": "出演する"
}
]
}
]
}
